import React from 'react'
import data from '../../data/data'
import options from '../../helpers/date'

function Fixture() {

    console.log(data.groupA.matches)

  return (
    <div className='fixture'>
        {
           data.groupA.matches.map( match => 
           <div id={match.id}>
                <img src={data.countries[match.local-1].flag} alt="Local Flag" />
                <p>
                    {data.countries[match.local-1].name} vs. {data.countries[match.visitor-1].name}
                </p>
                <img src={data.countries[match.visitor-1].flag} alt="Visitor Flag" />
                <p>{new Date(match.date).getDate()}/{new Date(match.date).getMonth()}: {match.stadium} Stadium at {new Date(match.date).getUTCHours()}:00hs</p>
           </div>)
        }

    </div>
  )
}

export default Fixture
