import React from 'react'
import './Group.css'
import Match from './Match'

function Group({ group, countries, id }) {

  const handleClick = () =>{
    fetch(`http://localhost:3001/api/get-group/`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        group: group.group,
        countries: group.countries
      })
    })
    .then(res => res.json())
    .then(data => console.log(data))
    .error(err => console.error(err))

    console.log(group.group)
  }

  console.log(group)

  return (
    <div className='group-stage-group-container' id={id}>
        <h1 className='group-stage-group'>Group { group.group }</h1>
        {
            group.matches.map( match => <Match id={match.matchid} match={match} countries={countries}/> )
        }
        <button onClick={handleClick}>Get Group {group.group}</button>
    </div>
  )
}

export default Group
